public static void GetColor(double p, double q,out double r,out double g,out double b)
{
	double bigX,bigY;
	double x=0,y=0;
	r = 0; g = 0; b = 255;
	for (int i = 0;i <= 64;i++)
	{
		bigX = x*x - y*y + p;
		bigY = x*y*x*y + q;
		if (double.IsNaN(bigX) || double.IsNaN(bigY))
		{
			b = i*6;
			return;
		}
		x = bigX;
		y = bigY;
	}
	return;
}
